// Script for WOW Animation Initialize
new WOW().init();

// Script for Product Carousel
$(".storySlider").slick({
  autoplay: true,
  autoplaySpeed: 5000,
  dots: false,
  arrows: true,
  infinite: true,
  slidesToShow: 7,
  slidesToScroll: 1,
  prevArrow: '<button type="button" class="slick-prev"><i class="fal fa-chevron-left"></i></button>',
  nextArrow: '<button type="button" class="slick-next"><i class="fal fa-chevron-right"></i></button>',
  responsive: [
    {
      breakpoint: 1199,
      settings: {
        slidesToShow: 3,
      },
    },
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 2,
        arrows: false,
      },
    },
    {
      breakpoint: 575,
      settings: {
        slidesToShow: 1,
        arrows: false,
      },
    },
  ],
});

