// Page RoleBack to Top
$(document).ready(function () {
  $(".roleback").click(function () {
    $("html, body").animate({ scrollTop: 0 }, 800);
    return false;
  });
});

// Go Down
$(function () {
  $(".goDown").on("click", function (e) {
    e.preventDefault();
    $("html, body").animate(
      {
        scrollTop: $(".aboutSec").offset().top,
      },
      500,
      "linear"
    );
  });
});

// Search Box Open
$(document).ready(function () {
  $(".searchBar input").focus(function () {
    $(".expandSearch").slideDown("slow");
    //return false;
  });
  $(".searchBar input").blur(function () {
    if (!$(this).val()) {
      $(".expandSearch").slideUp("slow");
    }
  });
}); //end

// Redirect to specific Tab
$(function () {
  var hash = window.location.hash;
  hash && $('ul.nav a[href="' + hash + '"]').tab("show");

  $(".nav-tabs a").click(function (e) {
    $(this).tab("show");
    var scrollmem = $("body").scrollTop();
    window.location.hash = this.hash;
    $("html,body").scrollTop(scrollmem);
  });
});


// Reply to Comments
$(".replyBtn").click(function () {
    $(this).closest('ul').next().slideToggle('active');
});


$(document).ready(function() {
    $("#message-text").emojioneArea({
        pickerPosition: "top",
        filtersPosition: "bottom",
        tonesStyle: "bullet"
    });
});
