// Script for Counter
$(document).ready(function () {
    $('.minus').click(function () {
        var $input = $(this).parent().find('input');
        var count = parseInt($input.val()) - 1;
        count = count < 1 ? 1 : count;
        $input.val(count);
        $input.change();
        return false;
    });
    $('.plus').click(function () {
        var $input = $(this).parent().find('input');
        $input.val(parseInt($input.val()) + 1);
        $input.change();
        return false;
    });
});


// Script for WOW Animation Initialize
new WOW().init();

// Script for Product Carousel
$('.reviewsCarousel').slick({
    autoplay: true,
    autoplaySpeed: 5000,
    dots: false,
    arrows: true,
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    prevArrow: '<button type="button" class="slick-prev"><i class="fas fa-angle-double-left"></i></button>',
    nextArrow: '<button type="button" class="slick-next"><i class="fas fa-angle-double-right"></i></button>',
    responsive: [{
        breakpoint: 1199,
        settings: {
            slidesToShow: 3
        }
    },
    {
        breakpoint: 768,
        settings: {
            slidesToShow: 2,
            arrows: false
        }
    },
    {
        breakpoint: 575,
        settings: {
            slidesToShow: 1,
            arrows: false
        }
    }
    ]
});

$('.slider-for').slick({
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    fade: true,
    asNavFor: '.slider-nav'
});
$('.slider-nav').slick({
    slidesToShow: 3,
    slidesToScroll: 1,
    asNavFor: '.slider-for',
    dots: false,
    arrows: false,
    centerMode: true,
    focusOnSelect: true,
    vertical: true,
    verticalSwiping: true,
    responsive: [{
        breakpoint: 575,
        settings: {
            slidesToShow: 2,
            arrows: false
        }
    }]
});

// Page RoleBack to Top
$(document).ready(function () {
    $('.roleback').click(function () {
        $("html, body").animate({ scrollTop: 0 }, 800);
        return false;
    });
});

// Go Down
$(function () {
    $('.goDown').on('click', function (e) {
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $(".aboutSec").offset().top
        }, 500, 'linear');
    });
});

// Search Box Open
$('.openSearch').click(function () {
    $(".searchBox").slideDown();
})
$('.searchClose').click(function () {
    $(".searchBox").slideUp();
})